
var Frb = require(".");
require("./dom");

var object = Frb.create(null, {
    apples: document.querySelector("#apples"),
    oranges: document.querySelector("#oranges"),
    fruit: document.querySelector("#fruit"),
    test: document.querySelector("#test")
}, {
    "apples.checked": {"<->": "state = 'apples'"},
    "oranges.checked": {"<->": "state = 'oranges'"},
    "state": {"<->": "fruit.value"},
    "fruit.classList.has('invalid')": {"<-": "state != 'apples' && state != 'oranges'"},
    "test.checked": {"<->": "state = 'apples'"},
});

console.log(object);

