
var Frb = require("frb");
var SortedSet = require("collections/sorted-set");
var List = require("collections/list");

var object = Frb.create(null, {
    body: document.body,
    message: "Hello, World!",
    set: new List(),
    array: []
}, {
    "body.classList.has('black')": {"<-": "black"},
    //"set.has('black')": {"<->": "black"},
    "body.innerHTML": {"<->": "black"}
});

object.black = true;
console.log(object.set.toArray());

setTimeout(function () {
    //object.set.delete('black');
    //object.black = false;
    document.body.innerHTML = false;
    console.log(object.set.toArray());
}, 500);

console.log(Frb.getBindings(object));

